    )
}

@Composable
private fun LeetCodeStatsSection(
    leetcodeUsername: String,
    solvedCount: Int?,
    solvedEasy: Int?,
    solvedMedium: Int?,
    solvedHard: Int?,
    easyTotal: Int?,
    mediumTotal: Int?,
    hardTotal: Int?,
    onLeetCodeUpdate: (String) -> Unit,
    onLeetCodeRefresh: () -> Unit
) {
    var showUpdateDialog by remember { mutableStateOf(false) }

    Box(
        modifier = Modifier
            .fillMaxWidth()
            .padding(top = 16.dp)
    ) {
        // Shadow Card
        Card(
            modifier = Modifier
                .matchParentSize()
                .offset(x = 6.dp, y = 6.dp),
            colors = CardDefaults.cardColors(containerColor = Color.Black),
            shape = RoundedCornerShape(16.dp),
            elevation = CardDefaults.cardElevation(0.dp)
        ) {}

        // Main Card
        Card(
            modifier = Modifier
                .border(2.dp, Color.Black, RoundedCornerShape(16.dp)),
            colors = CardDefaults.cardColors(containerColor = Color.White),
            shape = RoundedCornerShape(16.dp),
            elevation = CardDefaults.cardElevation(0.dp)
        ) {
            Column(
                horizontalAlignment = Alignment.CenterHorizontally,
                modifier = Modifier.padding(20.dp)
            ) {
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.SpaceBetween,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Text(
                        text = "LeetCode: $leetcodeUsername",
                        style = MaterialTheme.typography.titleMedium,
                        fontWeight = FontWeight.Bold
                    )

                    Row {
                        Icon(
                            imageVector = Icons.Default.Edit,
                            contentDescription = "Update username",
                            modifier = Modifier
                                .size(20.dp)
                                .clickable { showUpdateDialog = true }
                        )
                    }
                }

                Spacer(modifier = Modifier.height(16.dp))

                OverallProgressIndicator(solvedCount = solvedCount)

                Spacer(modifier = Modifier.height(16.dp))

                DifficultyIndicatorsRow(
                    solvedEasy = solvedEasy,
                    solvedMedium = solvedMedium,
                    solvedHard = solvedHard,
                    easyTotal = easyTotal,
                    mediumTotal = mediumTotal,
                    hardTotal = hardTotal
                )

                Spacer(modifier = Modifier.height(16.dp))

                ShadowButton(
                    text = "Refresh Stats",
                    onClick = onLeetCodeRefresh,
                    modifier = Modifier.fillMaxWidth(),
                    foregroundColor = Color(0xFF7B9EFF),
                    contentColor = Color.White
                )
            }
        }
    }

    if (showUpdateDialog) {
        LeetCodeUsernameUpdateDialog(
            currentUsername = leetcodeUsername,
            onDismiss = { showUpdateDialog = false },
            onUpdate = { newUsername ->
                onLeetCodeUpdate(newUsername)
                showUpdateDialog = false
            }
        )
    }
}

@Composable
private fun OverallProgressIndicator(
    solvedCount: Int?
) {
    Box(contentAlignment = Alignment.Center) {
        CircularProgressIndicator(
            progress = { (solvedCount?.coerceAtMost(2000) ?: 0) / 2000f },
            strokeWidth = 8.dp,
            modifier = Modifier.size(120.dp)
        )
        Text(
            text = "${solvedCount ?: 0}",
            style = MaterialTheme.typography.titleLarge,
            fontWeight = FontWeight.Bold
        )
    }
    Text("Problems Solved")
}

@Composable
private fun DifficultyIndicatorsRow(
    solvedEasy: Int?,
    solvedMedium: Int?,
    solvedHard: Int?,
    easyTotal: Int?,
    mediumTotal: Int?,
    hardTotal: Int?
) {
    Row(modifier = Modifier.fillMaxWidth()) {
        DifficultyIndicator(
            label = "Easy",
            count = solvedEasy,
            total = easyTotal,
            progressColor = Color(0xFF4CAF50),
            modifier = Modifier.weight(1f)
        )
        DifficultyIndicator(
            label = "Medium",
            count = solvedMedium,
            total = mediumTotal,
            progressColor = Color(0xFFFF9800),
            modifier = Modifier.weight(1f)
        )
        DifficultyIndicator(
            label = "Hard",
            count = solvedHard,
            total = hardTotal,
            progressColor = Color(0xFFF44336),
            modifier = Modifier.weight(1f)
        )
    }
}

